mapper.insert:
INSERT INTO achievement (
    project_id, score, grade, teacher_comment,
    evaluation_time, evaluator_id
) VALUES (
             #{projectId}, #{score}, #{grade}, #{teacherComment},
             #{evaluationTime}, #{evaluatorId}
         )

mapper.select:
SELECT * FROM achievement
WHERE evaluator_id = #{evaluatorId}
ORDER BY evaluation_time DESC
