mapper.insert:
INSERT INTO project (
    project_name, description, teacher_id, teacher_name,apply_time,
    start_time, end_time, status, create_time
) VALUES (
             #{projectName}, #{description}, #{teacherId}, #{teacherName}, #{applyTime},
             #{startTime}, #{endTime}, #{status}, #{createTime}
         )

mapper.update:
UPDATE project
SET status = #{status}
WHERE project_id = #{projectId}

UPDATE project
SET status = #{status},
    complete_time = #{completeTime}
WHERE project_id = #{projectId}

mapper.select:
SELECT p.*, u.real_name as teacherName FROM project p JOIN user u ON p.teacher_id = u.user_id WHERE p.teacher_id = #{teacherId} AND p.status IN
#{s}
ORDER BY p.create_time DESC
